import './App.css';
import Header from './components/header/Header.jsx';
import Footer from './components/footer/Footer.jsx';
import Overlay from './components/overlay/Overlay';
import React from 'react';
import axios from 'axios';
import { Routes, Route} from 'react-router-dom';
import Favorites from "./components/favorites/Favorites"
import Home from './components/Home';

export const AppContext = React.createContext({})

function App() {
    //состояние корзины
    const [overlayOpen, setOverlayOpen] = React.useState(false)
    //хранение данных туров
    const [tyrs, setTyrs] = React.useState([])
    //для хранения объектов корзины
    const [overlayItems, setOverlayItems] = React.useState([])
    //Для поиска 
    const [search, setSearch ] = React.useState('')
    //Для хранения избранных заявок 
    const [favorites, setFavorites] = React.useState([])

    React.useEffect(() => {
        async function axiosData(){

        const toursData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/tyrs')

        setTyrs = (toursData.data)

       const cartData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/cart')

       setOverlayItems=(cartData.data)

       const favoritesData = await axios.get('https://637f91ca2f8f56e28e904e7d.mockapi.io/favorites')
       setFavorites=(favoritesData.data)
        }

       axiosData();

    }, [])

    const deleteItems=(id)=>{
        console.log(id);
        axios.delete(`https://637f91ca2f8f56e28e904e7d.mockapi.io/cart/${id}`)
        setOverlayItems((objDelete)=> objDelete.filter(item => item.id !== id))
    }

    const isAdded =(myId)=>{
            return overlayItems.some((objIsAdded) => objIsAdded.myId === myId)
    }

    return (
        <AppContext.Provider
            value={
                {
                tyrs,
                setTyrs,
                overlayItems,
                setOverlayItems,
                favorites,
                setFavorites
            }
        }
        >
        <div className="app">

            {overlayOpen ? 
            <Overlay
             overlayProp={overlayItems} 
            closeOverlay={() => setOverlayOpen(false)} 
            deleteItems={deleteItems}
            />
                : null
            }



            <Header openOverlay={() => setOverlayOpen(true)} />
            <Routes>
                <Route path='/favorites'
                    element={
                        <Favorites
                        favorites ={favorites}
                        setFavorites={setFavorites}
                        item={tyrs}
                        overlayItems={overlayItems}
                        setOverlayItems={setOverlayItems}
                        />
                    }
                />

                <Route path='/'
                    element={
                        <Home
                            item={tyrs}
                            overlayItems={overlayItems}
                            setOverlayItems={setOverlayItems}
                            setSearch={setSearch}
                            search={search}
                            favorites={favorites}
                            setFavorites={setFavorites}
                        />
                    }
                />

            </Routes>
            <Footer />


        </div>

        </AppContext.Provider>
    )
}
export default App
