import ProductItem from "./item/ProductItem"
import style from './overlay.module.css';

const overlayObj=[
    {
        id:'1',
        title: "Название",
        price:"36 000",
        img:"/img/1.jpeg"
    },
    {
        id:'2',
        title: "Название 2",
        price:"36 000",
        img:"/img/1.jpeg"
    }, 
    {
        id:'3',
        title: "Название 3",
        price:"36 000",
        img:'/img/1.jpeg'
    }
]


const Overlay = () => {
    return (
        <div className={style.overlay}>
            <div className={style.product}>
                <div className={style.title_block}>
                    <h2>Заявки</h2>
                    <button className={style.close_btn} onClick={props.closeItem}>X</button>
                </div>

                <div className={style.product_list}>

                        { 
                                overlayObj.map(obj =>{

                                    return(
                                       <ProductItem key={obj.id} title={obj.title} price={obj.price} img={obj.img}/>
                                    )
                                })


                        }
                </div>

                <div className={style.total_price}>
                    <p className={style.total_price_text}>Итог:</p>
                    <p className={style.total_price_summ}>36 000</p>
                    <button>Оставить заявку</button>
                </div>

            </div>
        </div>
    )
}

export default Overlay
